import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { api } from "../services/api";
import '../styles/ApplyJobPages.css';

const ApplyJobPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { job } = location.state || {};
  
  // Form state for user details
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    gender: "",
    height: "",
    weight: "",
    marital_status: "",
    num_of_children: "",
    education: "",
    skills: "",
    interests: "",
    previous_jobs: "",
    looking_jobs: "",
    description: "",
    passport_status: ""
  });

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleApply = async () => {
    try {
      // Save user details in job_seekers table
      const seekerResponse = await api.post("/seekers/", formData);
      const seekerId = seekerResponse.data.seeker_id;

      // Save applied job in applied_jobs table
      await api.post("/apply-job/", {
        seeker_id: seekerId,
        job_id: job.job_id
      });

      alert("Successfully applied for the job!");
      navigate("/applied-jobs");

    } catch (error) {
      console.error("Error applying for job:", error);
      alert("Failed to apply.");
    }
  };

  return (
    <div className="apply-job-page">
      {job && (
        <>
          <h2>{job.job_title}</h2>
          <p>{job.job_description}</p>
          <p>Country: {job.country}</p>
          <p>Salary: {job.salary}</p>

          {/* Application Form */}
          <div>
            <h3>Enter Your Details</h3>
            <input type="text" placeholder="Name" name="name" onChange={handleChange} required />
            <input type="number" placeholder="Age" name="age" onChange={handleChange} required />
            <select name="gender" onChange={handleChange} required>
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
            </select>
            <input type="number" placeholder="Height (cm)" name="height" onChange={handleChange} required />
            <input type="number" placeholder="Weight (kg)" name="weight" onChange={handleChange} required />
            <select name="marital_status" onChange={handleChange} required>
              <option value="">Select Marital Status</option>
              <option value="Single">Single</option>
              <option value="Married">Married</option>
            </select>
            <input type="number" placeholder="Number of Children" name="num_of_children" onChange={handleChange} />
            <select name="education" onChange={handleChange} required>
              <option value="">Select Education</option>
              <option value="High School">High School</option>
              <option value="Diploma">Diploma</option>
              <option value="Degree">Degree</option>
            </select>
            <input type="text" placeholder="Skills" name="skills" onChange={handleChange} required />
            <input type="text" placeholder="Interests" name="interests" onChange={handleChange} required />
            <input type="text" placeholder="Previous Jobs" name="previous_jobs" onChange={handleChange} required />
            <input type="text" placeholder="Looking Jobs" name="looking_jobs" onChange={handleChange} required />
            <textarea placeholder="Description" name="description" onChange={handleChange} required></textarea>
            <select name="passport_status" onChange={handleChange} required>
              <option value="">Select Passport Status</option>
              <option value="Valid">Valid</option>
              <option value="Invalid">Invalid</option>
              <option value="Applied">Applied</option>
            </select>
          </div>

          <button onClick={handleApply}>Confirm Apply</button>
        </>
      )}
    </div>
  );
};

export default ApplyJobPage;
