import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import { api } from "../services/api";
import '../styles/ApplyJobPages.css';

const ApplyJobPage = () => {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [jobDetails, setJobDetails] = useState(null);
  const [cv, setCv] = useState(null);
  const [coverLetter, setCoverLetter] = useState(null);

  useEffect(() => {
    const fetchJobDetails = async () => {
      try {
        const response = await api.get(`/jobs/${jobId}`);
        setJobDetails(response.data);
      } catch (error) {
        console.error("Error fetching job details:", error);
      }
    };
    fetchJobDetails();
  }, [jobId]);

  // Update these handlers to set the actual File objects
  const handleCvChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setCv(e.target.files[0]);
      console.log("CV file selected:", e.target.files[0].name);
    }
  };

  const handleCoverLetterChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setCoverLetter(e.target.files[0]);
      console.log("Cover letter selected:", e.target.files[0].name);
    }
  };

  const handleApply = async () => {
    if (!cv) {
      alert("Please upload your CV before applying.");
      return;
    }
  
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Session expired. Please log in again.");
      localStorage.removeItem("token");
      window.location.href = "/login";
    }
  
    const decodedToken = jwtDecode(token);
    const userId = decodedToken.user_id;
  
    // Create a proper FormData object
    const formData = new FormData();
    formData.append("user_id", userId);
    formData.append("job_id", jobId);
    formData.append("cv", cv);  // This should be the File object from input
    if (coverLetter) {
      formData.append("cover_letter", coverLetter);
    }
  
    try {
      // Log the formData for debugging
      console.log("Submitting form data:");
      for (let pair of formData.entries()) {
        console.log(pair[0] + ': ' + pair[1]);
      }
  
      // Use axios with the correct configuration for FormData
      const response = await api.post("/apply-job/", formData, {
        headers: {
          "Authorization": `Bearer ${token}`,
          // Let the browser set the correct Content-Type with boundary
        }
      });
      
      console.log("Response:", response);
      alert("Applied successfully!");
      navigate("/applied-jobs");
    } catch (error) {
      console.error("Error applying for job:", error.response?.data || error);
      alert("Failed to apply: " + (error.response?.data?.detail || error.message));
    }
  };

  return (
    <div className="apply-job-page">
      {jobDetails && (
        <>
          <h2>{jobDetails.job_title}</h2>
          <p>{jobDetails.job_description}</p>
          <p>Country: {jobDetails.country}</p>
          <p>Skills Required: {jobDetails.skills_required}</p>
          <p>Salary: {jobDetails.salary}</p>

          <div className="file-upload">
            <label>Upload CV:</label>
            <input type="file" accept=".pdf,.docx" onChange={handleCvChange} />
          </div>

          <div className="file-upload">
            <label>Upload Cover Letter (Optional):</label>
            <input type="file" accept=".pdf,.docx" onChange={handleCoverLetterChange} />
          </div>

          <button onClick={handleApply}>Apply Now</button>
        </>
      )}
    </div>
  );
};

export default ApplyJobPage;
