import React, { useState } from "react";
import { api } from "../services/api";
import JobCard from "../components/JobCard"; // Reuse the JobCard component
import "../styles/RecommendationsPage.css"; // Add styling if needed

const RecommendationsPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    gender: "",
    height: "",
    weight: "",
    marital_status: "",
    num_of_children: "",
    education: "",
    skills: "",
    interests: "",
    previous_jobs: "",
    looking_jobs: "",
    description: "",
    passport_status: "",
  });

  const [recommendedJobs, setRecommendedJobs] = useState([]);
  const [showRecommendations, setShowRecommendations] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post("/recommendations/", formData);
      setRecommendedJobs(response.data);
      setShowRecommendations(true);
    } catch (error) {
      console.error("Error fetching recommendations:", error);
    }
  };

  return (
    <div className="recommendations-page">
      <h2>Get Your Job Recommendations</h2>
      <form onSubmit={handleSubmit} className="recommendation-form">
        {Object.keys(formData).map((key) => (
          <div key={key} className="form-group">
            <label>
              {key.replace("_", " ").toUpperCase()}:
              <input
                type={key === "age" || key === "height" || key === "weight" || key === "num_of_children" ? "number" : "text"}
                name={key}
                value={formData[key]}
                onChange={handleChange}
                required
              />
            </label>
          </div>
        ))}
        <button type="submit" className="submit-button">Get Recommendations</button>
      </form>

      {showRecommendations && (
        <div className="recommendations-section">
          <h3>Recommended Jobs</h3>
          <div className="job-cards-container">
            {recommendedJobs.map((job) => (
              <JobCard key={job.job_id} job={job} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default RecommendationsPage;
