import React, { useEffect, useState } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { api } from "../services/api";
import '../styles/ApplyJobPages.css';

const ApplyJobPage = () => {
  const { jobId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const isFromRecommendations = location.state?.isFromRecommendations;
  const [formData, setFormData] = useState(() => {
    return JSON.parse(localStorage.getItem("userData")) || {
      name: "", age: "", gender: "", height: "", weight: "", marital_status: "",
      num_of_children: "", education: "", skills: "", interests: "",
      previous_jobs: "", looking_jobs: "", description: "", passport_status: ""
    };
  });
  
  const [jobDetails, setJobDetails] = useState(null);

  useEffect(() => {
    const fetchJobDetails = async () => {
      try {
        const response = await api.get(`/jobs/${jobId}`);
        setJobDetails(response.data);
      } catch (error) {
        console.error("Error fetching job details:", error);
      }
    };
    fetchJobDetails();
  }, [jobId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleApply = async () => {
    try {
      let seekerId = localStorage.getItem("seekerId");
      if (!seekerId) {
        const response = await api.post("/apply-job/", { ...formData, job_id: jobId });
        seekerId = response.data.seeker_id;
        localStorage.setItem("seekerId", seekerId);
        localStorage.setItem("userData", JSON.stringify(formData));
      } else {
        await api.post("/apply-job/", { seeker_id: seekerId, job_id: jobId });
      }
      alert("Applied successfully!");
      navigate("/applied-jobs");
    } catch (error) {
      console.error("Error applying for job:", error);
    }
  };

  return (
    <div className="apply-job-page">
      {jobDetails && (
        <>
          <h2>{jobDetails.job_title}</h2>
          <p>{jobDetails.job_description}</p>
          <p>Country: {jobDetails.country}</p>
          <p>Skills Required: {jobDetails.skills_required}</p>
          <p>Salary: {jobDetails.salary}</p>
          {!isFromRecommendations && (
            <div>
              <h3>Enter Your Details</h3>
              <input type="text" name="name" placeholder="Name" onChange={handleChange} />
              <input type="number" name="age" placeholder="Age" onChange={handleChange} />
              <input type="text" name="gender" placeholder="Gender" onChange={handleChange} />
              <button onClick={handleApply}>Confirm Apply</button>
            </div>
          )}
          {isFromRecommendations && <button onClick={handleApply}>Confirm Apply</button>}
        </>
      )}
    </div>
  );
};

export default ApplyJobPage;
