import React, { useEffect, useState } from "react";
import { api } from "../services/api";
import { fetchJobSeekerProfile } from "../services/api";
import { useAuth } from "../context/AuthContext";
// import "../styles/ProfilePage.css";
import { useNavigate } from "react-router-dom";

const ProfilePage = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    const fetchProfile = async () => {
      try {
        console.log(`Fetching profile for user ID: ${user.user_id}`);
        // const response = await api.get(`/seekers/${user.seeker_id}`);
        const response = await fetchJobSeekerProfile(user.user_id);
        setProfile(response);
        setFormData(response);
        // setProfile(response.data);
        // setFormData(response.data);
      } catch (error) {
        console.error("Error fetching profile data:", error);
      }
    };
    fetchProfile();
  }, [user, navigate]);

  const handleEdit = () => setIsEditing(true);
  const handleCancel = () => {
    setIsEditing(false);
    setFormData(profile);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    try {
      await api.put(`/seekers/${user.seeker_id}`, formData);
      setProfile(formData);
      setIsEditing(false);
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  return (
    <div className="profile-container">
      <h2>Job Seeker Profile</h2>
      {profile ? (
        <div className="profile-card">
          <div className="profile-field">
            <strong>Name:</strong>
            {isEditing ? (
              <input type="text" name="name" value={formData.name || ""} onChange={handleChange} />
            ) : (
              <span>{profile.name || "Not Specified"}</span>
            )}
          </div>
          <div className="profile-field">
            <strong>Age:</strong>
            {isEditing ? (
              <input type="number" name="age" value={formData.age || ""} onChange={handleChange} />
            ) : (
              <span>{profile.age || "Not Specified"}</span>
            )}
          </div>
          <div className="profile-field">
            <strong>Gender:</strong>
            {isEditing ? (
              <select name="gender" value={formData.gender || ""} onChange={handleChange}>
                <option value="">Select</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
              </select>
            ) : (
              <span>{profile.gender || "Not Specified"}</span>
            )}
          </div>
          <div className="profile-field">
            <strong>Skills:</strong>
            {isEditing ? (
              <input type="text" name="skills" value={formData.skills || ""} onChange={handleChange} />
            ) : (
              <span>{profile.skills || "Not Specified"}</span>
            )}
          </div>
          <div className="profile-buttons">
            {isEditing ? (
              <>
                <button onClick={handleSave} className="save-btn">Save</button>
                <button onClick={handleCancel} className="cancel-btn">Cancel</button>
              </>
            ) : (
              <button onClick={handleEdit} className="edit-btn">Edit Profile</button>
            )}
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProfilePage;
