import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { fetchJobs } from "../services/api"; // Fetch jobs from API
import "../styles/Home.css";
import heroImage from "../assets/hero3.jpg"; // Add a hero background
import jobIcon1 from "../assets/job-icon1.png"; // Example job icons
import jobIcon2 from "../assets/job-icon2.png";
import jobIcon3 from "../assets/job-icon3.png";
import animationVideo from "../assets/hero-animation.mp4"; // Engaging animation video



const Home = () => {
  const navigate = useNavigate();
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const getJobs = async () => {
      try {
        const jobData = await fetchJobs();
        setJobs(jobData.slice(0, 4)); // Show only 4 recent jobs
      } catch (error) {
        console.error("Error fetching jobs:", error);
      }
    };
    getJobs();
  }, []);

  return (
    <div className="home-container">
      
      {/* Hero Section with Animation */}
      <section className="hero-section">
        <video autoPlay loop muted className="background-video">
          <source src={animationVideo} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <div className="overlay">
          <h1>Find Your Perfect Job</h1>
          <p>Get job recommendations that match your skills and interests.</p>
          <button className="explore-btn">Explore Jobs</button>
        </div>
      </section>

      {/* How It Works */}
      <section className="how-it-works">
        <h2>How It Works</h2>
        <div className="steps-container">
          <div className="step">
            <h3>1</h3>
            <p>Create a Profile</p>
          </div>
          <div className="step">
            <h3>2</h3>
            <p>Get Job Matches</p>
          </div>
          <div className="step">
            <h3>3</h3>
            <p>Apply & Get Hired</p>
          </div>
        </div>
      </section>

      {/* Job Categories */}
      <section className="job-categories">
        <h2>Popular Job Categories</h2>
        <div className="categories-grid">
          <div className="category">Housekeeping</div>
          <div className="category">Construction</div>
          <div className="category">Driving</div>
        </div>
      </section>

      {/* Job Listings */}
      <section className="job-listings">
        <h2>Latest Job Openings</h2>
        <div className="job-cards-container">
          {/* Job Cards will be mapped here */}
        </div>
        <button className="view-more-btn">View More Jobs</button>
      </section>

      {/* Background Section with Handshake Image */}
      <section className="background-section">
        <div className="content-overlay">
          <h2>Why Choose Our Platform?</h2>
          <ul>
            <li>✅ AI-powered job recommendations</li>
            <li>✅ Verified employers and agencies</li>
            <li>✅ Easy and fast application process</li>
            <li>✅ 24/7 customer support</li>
          </ul>
        </div>
      </section>

      {/* Call to Action */}
      <section className="cta-section">
        <h2>Ready to Find Your Dream Job?</h2>
        <button className="cta-btn">Get Started Now</button>
      </section>

    </div>
  );
};

export default Home;
