import React, { useEffect, useState } from "react";
import { fetchJobs } from "../services/api";
import JobCard from "../components/JobCard";
import '../styles/JobsPage.css';
import '../styles/custom-jobs-layout.css';

const JobsPage = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getJobs = async () => {
      try {
        setLoading(true);
        const jobsData = await fetchJobs();
        setJobs(jobsData);
        setError(null);
      } catch (error) {
        console.error("Error fetching jobs:", error);
        setError("Failed to load jobs. Please try again later.");
      } finally {
        setLoading(false);
      }
    };
  
    getJobs();
  }, []);

  if (loading) {
    return <div className="jobs-container">Loading available jobs...</div>;
  }

  if (error) {
    return <div className="jobs-container">{error}</div>;
  }

  return (
    <div className="jobs-container" id="jobs-container">
      <h2>Available Jobs</h2>
      {/* Adding the custom-jobs-list class for higher specificity */}
      <div className="jobs-list custom-jobs-list">
        {jobs.map((job) => (
          // Wrap each JobCard in a div with a specific class for targeting
          <div className="job-card-wrapper" key={job.job_id}>
            <JobCard job={job} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default JobsPage;
