import React, { useEffect, useState } from "react";
import { api } from "../services/api";
import { useAuth } from "../context/AuthContext"; // Ensure correct import
import '../styles/ApplyJobPages.css';

const AppliedJobsPage = () => {
  const auth = useAuth(); // ✅ Use full object instead of destructuring

  if (!auth) {
    console.error("Auth context is not available");
    return <p>Error: Authentication not available</p>;
  }

  const { user } = auth;  // ✅ Safely extract user after checking auth
  const seekerId = user?.seeker_id; // Ensure seekerId is obtained correctly
  const [appliedJobs, setAppliedJobs] = useState([]);

  useEffect(() => {
    if (!seekerId) return;

    const fetchAppliedJobs = async () => {
      try {
        const response = await api.get(`/applied-jobs/${seekerId}`);
        setAppliedJobs(response.data);
      } catch (error) {
        console.error("Error fetching applied jobs:", error);
      }
    };

    fetchAppliedJobs();
  }, [seekerId]);

  return (
    <div className="applied-jobs-page">
      <h2>Applied Jobs</h2>
      {appliedJobs.length > 0 ? (
        <ul>
          {appliedJobs.map((job) => (
            <li key={job.job_id}>
              <h3>{job.job_title}</h3>
              <p>{job.country}</p>
              <p>Applied on: {new Date(job.applied_at).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No jobs applied yet.</p>
      )}
    </div>
  );
};

export default AppliedJobsPage;
