import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { api } from "../services/api";
import '../styles/ApplyJobPages.css';

const ApplyJobPage = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { job } = location.state || {};

    // Form state for user details
    const [formData, setFormData] = useState({
        name: "",
        age: "",
        gender: "",
        height: "",
        weight: "",
        marital_status: "",
        num_of_children: "",
        education: "",
        skills: "",
        interests: "",
        previous_jobs: "",
        looking_jobs: "",
        description: "",
        passport_status: ""
    });

    // Handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Handle form submission
    const handleApply = async () => {
        try {
            console.log("üöÄ Starting Apply Process...");

            // Step 1: Save user details in job_seekers table
            const seekerResponse = await api.post("/seekers/", formData);
            console.log("‚úÖ Seeker Created Response:", seekerResponse.data);

            // Extract seeker_id correctly
            const seekerId = seekerResponse.data.seeker_id;
            if (!seekerId) {
                console.error("‚ùå Seeker ID missing, stopping process.");
                alert("Seeker creation failed.");
                return;
            }

            console.log("üîπ Seeker ID found:", seekerId);

            // Step 2: Prepare apply-job request
            const applyPayload = { seeker_id: seekerId, job_id: job.job_id };
            console.log("üì§ Sending Apply Job Payload:", applyPayload);

            // Step 3: Send Apply Job request
            const applyResponse = await api.post("/apply-job/", applyPayload);
            console.log("‚úÖ Job Applied Response:", applyResponse.data);

            alert("Successfully applied for the job!");
            navigate("/applied-jobs");

        } catch (error) {
            console.error("‚ùå Error applying for job:", error.response ? error.response.data : error);
            alert("Failed to apply.");
        }
    };

    return (
        <div className="apply-job-page">
            {job && (
                <>
                    <h2>{job.job_title}</h2>
                    <p>{job.job_description}</p>
                    <p>Country: {job.country}</p>
                    <p>Salary: {job.salary}</p>

                    {/* Application Form */}
                    <div>
                        <h3>Enter Your Details</h3>
                        <input type="text" placeholder="Name" name="name" onChange={handleChange} required />
                        <input type="number" placeholder="Age" name="age" onChange={handleChange} required />
                        <select name="gender" onChange={handleChange} required>
                            <option value="">Select Gender</option>
                            <option value="Male">Male</option>
                            <option value="Female">Female</option>
                        </select>
                        <input type="number" placeholder="Height (cm)" name="height" onChange={handleChange} required />
                        <input type="number" placeholder="Weight (kg)" name="weight" onChange={handleChange} required />
                        <button onClick={handleApply}>Confirm Apply</button>
                    </div>
                </>
            )}
        </div>
    );
};

export default ApplyJobPage;
