import React, { useEffect, useState } from "react";
import { fetchJobs } from "../services/api";

const JobsPage = () => {
  const [jobs, setJobs] = useState([]);

  // useEffect(() => {
  //   const getJobs = async () => {
  //     try {
  //       const jobsData = await fetchJobs();
  //       console.log("Fetched jobs:", jobsData); // Log API data
  //       setJobs(jobsData);
  //     } catch (error) {
  //       console.error("Error fetching jobs:", error);
  //     }
  //   };
  
  //   getJobs();
  // }, []);

  useEffect(() => {
    const getJobs = async () => {
      try {
        const jobsData = await fetchJobs();
        console.log("Fetched jobs:", jobsData); // Log API data
        setJobs(jobsData);
      } catch (error) {
        console.error("Error fetching jobs:", error);
      }
    };

    getJobs();
  }, []);

  return (
    <div>
      <h2>Available Jobs</h2>
      {jobs.length > 0 ? (
        <table border="1" style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>Job Title</th>
              <th>Country</th>
              <th>Description</th>
              <th>Skills Required</th>
              <th>Salary</th>
              <th>Working Hours</th>
            </tr>
          </thead>
          <tbody>
            {jobs.map((job) => (
              <tr key={job.job_id}>
                <td>{job.job_title}</td>
                <td>{job.country}</td>
                <td>{job.job_description}</td>
                <td>{job.skills_required}</td>
                <td>{job.salary}</td>
                <td>{job.working_hours}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No jobs available at the moment.</p>
      )}
    </div>
  );
};

export default JobsPage;
