import React, { useEffect, useState } from "react";
// import { api } from "../services/api";
import { fetchJobSeekerProfile, updateJobSeekerProfile, createJobSeekerProfile } from "../services/api";
import { useAuth } from "../context/AuthContext";
import "../styles/ProfilePage.css";
import { useNavigate } from "react-router-dom";

const ProfilePage = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    age: 0,
    gender: "",
    height: 0,
    weight: 0,
    marital_status: "",
    num_of_children: 0,
    education: "",
    skills: "",
    interests: "",
    previous_jobs: "",
    looking_jobs: "",
    description: "",
    passport_status: "",
  });

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

  const fetchProfile = async () => {
    try {
      console.log(`Fetching profile for user ID: ${user.user_id}`);
      // const response = await api.get(`/seekers/${user.seeker_id}`);
      const response = await fetchJobSeekerProfile(user.user_id);
      setProfile(response);
      setFormData(response);
      // setProfile(response.data);
      // setFormData(response.data);
    } catch (error) {
      console.error("Error fetching profile data:", error);
    }
  };
  fetchProfile();
}, [user, navigate]);

  const handleEdit = () => setIsEditing(true);
  const handleCancel = () => {
    setIsEditing(false);
    setFormData(profile || {});
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // const handleSave = async (e) => {
  //   e.preventDefault();
  //   try {
  //       if (profile) {
  //           await updateJobSeekerProfile(user.user_id, formData);
  //           alert("Profile updated successfully.");
  //       } else {
  //           await createJobSeekerProfile(user.user_id, formData);
  //           alert("Profile created successfully.");
  //       }
  //       setIsEditing(false);
  //       setProfile(formData);
  //   } catch (err) {
  //       console.error("Error updating profile:", err);
  //   }
  // };

  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...formData, user_id: user.user_id }; // Ensure user_id is included in request
  
      if (profile) {
        await updateJobSeekerProfile(user.user_id, payload);
        alert("Profile updated successfully.");
      } else {
        await createJobSeekerProfile(payload);
        alert("Profile created successfully.");
      }
  
      setIsEditing(false);
      setProfile(payload);
    } catch (err) {
      console.error("Error updating profile:", err);
    }
  };
  

  return (
    <div className="profile-container">
      <h2>Job Seeker Profile</h2>
      <div className="profile-card">
        <div className="profile-field">
          <strong>Name:</strong>
          {isEditing ? (
            <input type="text" name="name" value={formData.name} onChange={handleChange} />
          ) : (
            <span>{profile?.name || "Not Specified"}</span>
          )}
        </div>
        <div className="profile-field">
          <strong>Age:</strong>
          {isEditing ? (
            <input type="number" name="age" value={formData.age} onChange={handleChange} />
          ) : (
            <span>{profile?.age || "Not Specified"}</span>
          )}
        </div>
        <div className="profile-field">
          <strong>Gender:</strong>
          {isEditing ? (
            <select name="gender" value={formData.gender} onChange={handleChange}>
              <option value="">Select</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
            </select>
          ) : (
            <span>{profile?.gender || "Not Specified"}</span>
          )}
        </div>
        <div className="profile-field">
          <strong>Height:</strong>
          {isEditing ? (
            <input type="number" name="height" value={formData.height} onChange={handleChange} />
          ) : (
            <span>{profile?.height || "Not Specified"} cm</span>
          )}
        </div>
        <div className="profile-field">
          <strong>Weight:</strong>
          {isEditing ? (
            <input type="number" name="weight" value={formData.weight} onChange={handleChange} />
          ) : (
            <span>{profile?.weight || "Not Specified"} kg</span>
          )}
        </div>
        <div className="profile-field">
          <strong>Marital Status:</strong>
          {isEditing ? (
            <select name="marital_status" value={formData.marital_status} onChange={handleChange}>
              <option value="">Select</option>
              <option value="Male">Single</option>
              <option value="Female">Married</option>
            </select>
          ) : (
            <span>{profile?.marital_status || "Not Specified"}</span>
          )}
        </div>
        <div className="profile-field">
          <strong>Number of Children:</strong>
          {isEditing ? (
            <input type="number" name="num_of_children" value={formData.num_of_children} onChange={handleChange} />
          ) : (
            <span>{profile?.num_of_children || "Not Specified"}</span>
          )}
        </div>


        <div className="profile-field">
          <strong>Skills:</strong>
          {isEditing ? (
            <input type="text" name="skills" value={formData.skills} onChange={handleChange} />
          ) : (
            <span>{profile?.skills || "Not Specified"}</span>
          )}
        </div>
        <div className="profile-buttons">
          {isEditing ? (
            <>
              <button onClick={handleSave} className="save-btn">Save</button>
              <button onClick={handleCancel} className="cancel-btn">Cancel</button>
            </>
          ) : (
            <button onClick={handleEdit} className="edit-btn">Edit Profile</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
