import React from "react";
import DashboardGuest from "./DashboardGuest";
import DashboardRecruiter from "./DashboardRecruiter";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "../styles/Navbar.css";

const Dashboard = () => {
  const { user } = useAuth();

  if (!user) {
    return <DashboardGuest />; // Show Guest Dashboard if no user is logged in
  }

  if (user.role === "recruiter") {
    return <DashboardRecruiter />; // Show Recruiter Dashboard if role is recruiter
  }

  return (
    <nav className="navbar">
      <h1><Link to="/" className="globalhire">GlobalHire</Link></h1>
      <ul>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/jobs">Jobs</Link></li>
        <li><Link to="/recommendations">Recommendations</Link></li>
        <li><Link to="/applied-jobs">Applied Jobs</Link></li>
        <li><Link to="/profile">Profile</Link></li>
        <li><Link to="/"><button onClick={logout}>Logout</button></Link></li>
        {/* <li><button onClick={() => localStorage.clear()}>Logout</button></li> */}
      </ul>
    </nav>
  );
};

export default Dashboard;
