import React, { useEffect, useState } from "react";
import { fetchJobSeekerProfile, updateJobSeekerProfile } from "../services/api";
import { useAuth } from "../context/AuthContext";

const ProfilePage = () => {
  const { user } = useAuth();
  const [profile, setProfile] = useState({});
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    if (!user) return;
    
    const fetchProfile = async () => {
      try {
        const profileData = await fetchJobSeekerProfile(user.user_id);
        setProfile(profileData);
        setFormData(profileData);
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    fetchProfile();
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = async () => {
    try {
      const updatedProfile = await updateJobSeekerProfile(user.user_id, formData);
      setProfile(updatedProfile);
      setEditing(false);
      alert("Profile updated successfully!");
    } catch (error) {
      alert("Failed to update profile.");
    }
  };

  return (
    <div className="container">
      <h2>Your Profile</h2>
      {editing ? (
        <div>
          <input type="text" name="name" value={formData.name || ""} onChange={handleChange} placeholder="Name" />
          <input type="number" name="age" value={formData.age || ""} onChange={handleChange} placeholder="Age" />
          <input type="text" name="gender" value={formData.gender || ""} onChange={handleChange} placeholder="Gender" />
          <input type="text" name="skills" value={formData.skills || ""} onChange={handleChange} placeholder="Skills" />
          <textarea name="description" value={formData.description || ""} onChange={handleChange} placeholder="Description" />
          <button onClick={handleSave}>Save</button>
          <button onClick={() => setEditing(false)}>Cancel</button>
        </div>
      ) : (
        <div>
          <p><strong>Name:</strong> {profile.name}</p>
          <p><strong>Age:</strong> {profile.age}</p>
          <p><strong>Gender:</strong> {profile.gender}</p>
          <p><strong>Skills:</strong> {profile.skills}</p>
          <p><strong>Description:</strong> {profile.description}</p>
          <button onClick={() => setEditing(true)}>Edit Profile</button>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
